// hooks/useUserId.js
import { useMemo } from 'react';
import { jwtDecode } from 'jwt-decode';   // â† curly braces!

export function useUserId() {
  const token = localStorage.getItem('accessToken');

  return useMemo(() => {
    if (!token) return null;

    try {
      const { userId, exp } = jwtDecode(token);
      const payload = jwtDecode(token);
      // â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€  ðŸ‘€  ÑÐ¼Ð¾Ñ‚Ñ€Ð¸Ð¼ ÑÐ¾Ð´ÐµÑ€Ð¶Ð¸Ð¼Ð¾Ðµ Ð² DevTools  â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
      console.log('JWT payload:', payload); // works in v4+
      if (Date.now() >= exp * 1000) return null;
      return userId ?? null;
    } catch {
      return null;
    }
  }, [token]);
}
